import { GiphyFetch } from '@giphy/js-fetch-api'
import {useState} from 'react'
import TextList from './components/TextList'
import Error from './components/Error'
import './App.css';

const giphy = new GiphyFetch(process.env.REACT_APP_GIPHY_KEY)

function App() {
  const [text, setText] = useState('')
  const [results, setResults] = useState([])
  const [err, setErr] = useState(false)

  const handleInput = (e) => {
    setText(e.target.value)
  }

  const handleKeyDown = (e) => {
    if(e.key === 'Enter'){
      handleSubmit(e);
    }
  }

  const handleSubmit = (e) => {
    if(text.length === 0) {

      //set error state to true
      setErr(true)
      return
    }

    console.log(text)

    const apiCall = async () => {
      const res = await giphy.animate(text, {limit: 20})
      console.log(res.data)
      setResults(res.data)
    }

    apiCall()
    setText('')
    setErr(false)

  }

  return (
    <div className="App">
      <h1 className="font-bold text-xl my-5" >Animated Text Generator</h1>
      <h3 className="mb-3">Type text into the form and hit submit</h3>
      <input className="border-2 border-gray-300 mr-3" value={text} onChange={handleInput} onKeyDown={handleKeyDown} />
      <button className='submit-btn' onClick={handleSubmit}>Submit</button>
      <Error isError={err} text="need length longer than 0 for input"/>
      {results && <TextList gifs={results} />}

    </div>
  );
}
export default App;
